query {
  contestSections {
    id
    name
    description
    tag {
      text
    }
    totalContestCount
    displayContests {
      contestType
      isPartnerContest
      contestName
      contestCategory
      contestSize
      currentSize
      entryFee {
        amount
        symbol
      }
      effectiveEntryFee {
        amount
        symbol
      }
      hasJoined
      _id
      inviteCode
      isInfiniteEntry
      isGuaranteed
      isMultipleEntry
      isRecommended
      numberOfWinners
      prizeAmount {
        amount
        symbol
      }
      prizeDisplayText
      showInvite
      match {
        id
        status
      }
      tour {
        id
        name
      }
      site
    }
  }
}
