query ContestHomeQuery($site: String!, $tourId: Int!, $matchId: Int!) {
  contestSections(site: $site, matchId: $matchId, tourId: $tourId, withPromotions: true) {
    __typename
    displayContestCount
    totalContestCount
    tag {
      __typename
      text
    }
    displayContests {
      __typename
      ...ContestItem
    }
    description
    name
    id
  }
}

fragment ContestItem on Contest {
  __typename
  contestName
  contestCategory
  contestType
  contestSize
  currentSize
  entryFee {
    __typename
    amount
    symbol
  }
  _id
  inviteCode
  isInfiniteEntry
  isPartnerContest
  isGuaranteed
  isMultipleEntry
  prizeDisplayText
  numberOfWinners
  winnerPercent
  isMultipleEntry
  maxAllowedTeams
  winnerBreakup(limit: 1) {
    __typename
    prizeDisplayText
  }
  prizeAmount {
    __typename
    amount
    symbol
  }
  isFreeEntry
  effectiveEntryFee {
    __typename
    amount
  }
  match {
    __typename
    id
    status
  }
  tour {
    __typename
    id
    name
  }
  site
  joinedTeamsCount
  hasJoined
}
